import React from 'react'
import * as Drei from "@react-three/drei";
import { Html, Stats } from "@react-three/drei";

// 文字数をカウント (半角に対応)
function count(text){
    var length = 0.0;
    for(var i=0; i < text.length; i++) {
        text[i].match(/[ -~]/) ? length += 0.5 : length += 1.0;
    }
    return length;
}

export const Model3D = ({value}) => {
    const { scene } = Drei.useGLTF(value.input_path);
    const namePos = [value.pos[0], value.pos[1]+5, value.pos[2]]
    return (
    <mesh receiveShadow>
        <Html position={namePos} center={true} className="innerText" style={{width: count(value.name)+1+'em'}} distanceFactor={30} occlude={true}>
            <p>{value.name}</p>
        </Html>
        <primitive  position={[value.pos.x*10]} object={scene} roughness={0} smoothness={10}/>
    </mesh>        
    )
}
