import React from 'react'
import * as Fiber from '@react-three/fiber';
import * as Drei from "@react-three/drei";
import { Model } from './Model';


Drei.softShadows()
export const Draw3D = ({itemList}) => {
    function draw(){

    }

    return (
        <React.Suspense fallback={<span>loading...</span>}>

            <Fiber.Canvas shadows={true} shadowMap>
                <Drei.PerspectiveCamera/>
                <Drei.OrbitControls enablePan={true} enableZoom={true} enableRotate={true} />
                <Drei.Environment preset="city" />
                <ambientLight intensity={0.4} />
                <directionalLight
                    castShadow
                    intensity={0.3}
                    position={[0, 0, 100]}
                    shadow-mapSize-height={1024}
                    shadow-mapSize-width={1024}
                />
                    {itemList.map((value, key) => {
                        return (
                            <Model input_path={value} key={key}/>
                        )
                    })}
                    <Drei.ContactShadows position={[0, 0, 0]} opacity={0.4} width={500} height={500} blur={0.2} far={4.5} />
                    <Drei.BakeShadows />
            </Fiber.Canvas>
        </React.Suspense>
    );
};
