import React from 'react'
import * as Drei from "@react-three/drei";
import { Html, Stats } from "@react-three/drei";
import { DragControls } from 'three/examples/jsm/controls/DragControls'
import { Canvas, useThree } from "react-three-fiber"

// 文字数をカウント (半角に対応)
function count(text){
    var length = 0.0;
    for(var i=0; i < text.length; i++) {
        text[i].match(/[ -~]/) ? length += 0.5 : length += 1.0;
    }
    return length;
}

export const Model2D = ({value}) => {
    const { scene } = Drei.useGLTF(value.input_path);
    const { camera, gl, OrbitControls} = useThree()
    const cameraControls = new OrbitControls(camera, gl.domElement);
    const dragControls = new DragControls(scene, camera, gl.domElement);
    dragControls.enabled = true;
    dragControls.addEventListener( 'dragstart', function ( event ) {
        cameraControls.enabled = false;
    } );
    dragControls.addEventListener( 'dragend', function ( event ) {
        cameraControls.enabled = true;
    } );
    return (
    <mesh receiveShadow>
        <Html position={[value.x/10, value.y/10+5, value.z/10]} center={true} className="innerText" style={{width: count(value.name)+1+'em'}} distanceFactor={30} occlude={true}>
            <p>{value.name}</p>
        </Html>
        <primitive  position={[value.x/10, value.y/10, value.z/10]} object={scene} roughness={0} smoothness={10}/>
    </mesh>        
    )
}
