import React from 'react'
import * as Fiber from '@react-three/fiber';
import * as Drei from "@react-three/drei";
import { Model2D } from './Model2D';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';


Drei.softShadows()
export const Draw2D = ({itemList}) => {
    const [dragging, setDragging] = useState()

    return (
        <React.Suspense centered fallback={
            <Box sx={{display: 'flex', alignItems: 'center', justifyContent: 'center'}}><CircularProgress /></Box>
        }>
            <Fiber.Canvas>
                {itemList.map((value, key) => {
                    return (
                        <Model2D value={value} key={key}/>
                    )
                })}
                <Drei.OrbitControls minZoom={5} maxZoom={50} enabled={!dragging}/>
                <Drei.OrbitControls makeDefault position={[0, 40, 200]} />
            </Fiber.Canvas>
        </React.Suspense>
    );
};
