import React, {useState, useRef} from 'react'
import { useDrag } from "@use-gesture/react";
import * as Drei from "@react-three/drei";
import { Html, Stats } from "@react-three/drei";
import { DragControls } from 'three/examples/jsm/controls/DragControls';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import { Canvas, extend, useThree } from "react-three-fiber"
import * as THREE from 'three'
import ReactDOM from 'react-dom'

// 文字数をカウント (半角に対応)
function count(text){
    var length = 0.0;
    for(var i=0; i < text.length; i++) {
        text[i].match(/[ -~]/) ? length += 0.5 : length += 1.0;
    }
    return length;
}

extend({ DragControls })

export const Model2D = ({ value, setDragging, plane }) => {
    const [pos, setPos] = useState([0, 1, 0]);
    const {box} = new THREE.BoxGeometry( 1, 1, 1 );
    const [spring, api] = useSpring(() => ({
        // position: [0, 0, 0],
        position: pos,
        scale: 1,
        rotation: [0, 0, 0],
        config: { friction: 10 }
      }));

    var _pos = new THREE.Vector3();
    const dragObj = useRef();
    const bind = useDrag(
        ({active, movement: [x, y], timestamp, event}) => {
            if(active) {
                event.ray.intersectPlane(plane, _pos);
                setPos([_pos.x, _pos.y, _pos.z]);
                console.log(_pos);
            }
            setDragging(active);
            api.start({
                position: pos,
                scale: active ? 1.2 : 1
            });

        return timestamp;
        }
    );

    return (
    <mesh {...bind()}>
        <Html position={[value.x/10, value.y/10+5, value.z/10]} center={true} className="innerText" style={{width: count(value.name)+1+'em'}} distanceFactor={30} occlude={true}>
            <p>{value.name}</p>
        </Html>
        <Drei.Box  obejct={box} ref={dragObj}/>
    </mesh>       
    )
}
