import React from 'react'
import * as Drei from "@react-three/drei";
import { Html, Stats } from "@react-three/drei";

// 文字数をカウント (半角に対応)
function count(text){
    var length = 0.0;
    for(var i=0; i < text.length; i++) {
        text[i].match(/[ -~]/) ? length += 0.5 : length += 1.0;
    }
    return length;
}

export const Model = () => {
    const { scene } = Drei.useGLTF("./models/laser.glb");
    const name = "epgpeorgergpなまえまｎじゃふぃえerogepog";
    return (
    <group>
        <Html position={[-count(name),3,0]} className="innerText" style={{width: count(name)+1+'em'}} distanceFactor={20} occlude={true}>
            <p>{name}</p>
        </Html>
        <primitive position={[0,0,0]} object={scene} />
    </group>        
    )
}
