import React from 'react'
import Typography from '@mui/material/Typography';

export const Detail = () => {
    const useStyles = makeStyles({
        dark: {
            background: "#282c34", 
            color: '#DDDDDD',
        },
        gray: {
            background: "#383c44", 
            color: '#DDDDDD',
        }
    });
    const style = useStyles();


    const Delete = (id) => {
        /* タスクを削除する */
        setItemList(itemList.filter((item) => item.id !== id));
    }
    
    return (
        <div className="detail">
            <ul className="coordX detailInput">
                <Typography>x</Typography>
                <input type="number" />
            </ul>
            <ul className="coordY detailInput">
                <Typography>y</Typography>
                <input type="number" />
            </ul>
            <ul className="coordZ detailInput">
                <Typography>z</Typography>
                <input type="number" />
            </ul>
            <ul className="angle detailInput">
                <Typography>角度</Typography>
                <input type="number"/>
            </ul>
            <ul>
                <Tooltip onClick={() => Delete(item.id)} title="Remove" >
                    <IconButton className={style.dark}>
                        <Typography><DeleteIcon className={style.dark}/></Typography>
                    </IconButton>
                </Tooltip>
            </ul>
        </div>
    )
}
