import React, {useState, useRef} from 'react'
import * as Drei from "@react-three/drei";
import { Html, Stats } from "@react-three/drei";

// 文字数をカウント (半角に対応)
function count(text){
    var length = 0.0;
    for(var i=0; i < text.length; i++) {
        text[i].match(/[ -~]/) ? length += 0.5 : length += 1.0;
    }
    return length;
}

export const Model3D = ({value}) => {
    console.log(value.pos);
    const [pos, setPos] = useState(Math.floor(Math.round(value.pos[0])), Math.floor(Math.round(value.pos[1])), Math.floor(Math.round(value.pos[2])));
    const { scene } = Drei.useGLTF(value.input_path);
    return (
    <mesh receiveShadow>
        <Html position={[pos[0]*10, pos[1]*10+5, pos[2]*10]} center={true} className="innerText" style={{width: count(value.name)+1+'em'}} distanceFactor={30} occlude={true}>
            <p>{value.name}</p>
        </Html>
        <primitive position={pos} object={scene} roughness={0} smoothness={10} />

    </mesh>        
    )
}
