import React, {useState}from 'react';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import { makeStyles } from '@mui/styles';


const useStyles = makeStyles({
    dark: {
        background: "#282c34", 
        color: '#DDDDDD',
        fontWeight: ''
    },
    selected: {
        background: "orange",
    },
    indicator: {
        color:orange
    } 

});

export const Tabbar = ({value, setValue}) => {
    const style = useStyles();

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    return (
        <Box sx={{ borderBottom: 1, borderColor: 'divider', fontWeight: 'light'}} >
            <Tabs 
                value={value}
                onChange={handleChange}
                textColor={style.selected}
                indicatorColor={style.indicator}
            >
                <Tab label="2D" value="2D" className={style.dark}/>
                <Tab label="3D" value="3D" className={style.dark}/>
            </Tabs>
        </Box>

    );
}
