import React from 'react'
import * as Drei from "@react-three/drei";
import { Html, Stats } from "@react-three/drei";

// 文字数をカウント (半角に対応)
function count(text){
    var length = 0.0;
    for(var i=0; i < text.length; i++) {
        text[i].match(/[ -~]/) ? length += 0.5 : length += 1.0;
    }
    return length;
}

export const Model = () => {
    var scene = [];
    scene.push(Drei.useGLTF("./models/map_500.glb"));
    scene.push(Drei.useGLTF("./models/map_activity.glb"));
    /*scene += Drei.useGLTF("./models/map_administration.glb");
