    scene += Drei.useGLTF("./models/map_carrer.glb");
    scene += Drei.useGLTF("./models/map_dormitory.glb");
    scene += Drei.useGLTF("./models/map_education.glb");
    scene += Drei.useGLTF("./models/map_gym.glb");
    scene += Drei.useGLTF("./models/map_incubation.glb");
    scene += Drei.useGLTF("./models/map_isc.glb");
    scene += Drei.useGLTF("./models/map_lecture.glb");
    scene += Drei.useGLTF("./models/map_lecture_large.glb");
    scene += Drei.useGLTF("./models/map_lecture2.glb");
    scene += Drei.useGLTF("./models/map_library.glb");
    scene += Drei.useGLTF("./models/map_machine_workshop.glb");
    scene += Drei.useGLTF("./models/map_MILAiS.glb");
    scene += Drei.useGLTF("./models/map_nature.glb");
    scene += Drei.useGLTF("./models/map_research.glb");
    scene += Drei.useGLTF("./models/map_research_satellite.glb");
    scene += Drei.useGLTF("./models/map_swimming_pool.glb");
    scene += Drei.useGLTF("./models/map_workshop.glb");
*/
    const name = "レーザー";
    return (
    <group>
        {scene.map((value, key) => {
                    return (
                        <Html position={[0,5,0]} className="innerText" style={{width: count(name)+1+'em'}} distanceFactor={30} occlude={true}>
            <p>{name}</p>
        </Html>         
                    )
                })}

        <primitive position={[0,0,0]} object={scene} />
    </group>        
    )
}
