import React from 'react'
import * as Drei from "@react-three/drei";
import { Html, Stats } from "@react-three/drei";
import { useControls } from 'leva'

// 文字数をカウント (半角に対応)
function count(text){
    var length = 0.0;
    for(var i=0; i < text.length; i++) {
        text[i].match(/[ -~]/) ? length += 0.5 : length += 1.0;
    }
    return length;
}

export const Model3D = ({value}) => {
    const { scene } = Drei.useGLTF(value.input_path);
    const materialProps = useControls({
        thickness: { value: 5, min: 0, max: 20 },
        roughness: { value: 0, min: 0, max: 1, step: 0.1 },
        clearcoat: { value: 1, min: 0, max: 1, step: 0.1 },
        clearcoatRoughness: { value: 0, min: 0, max: 1, step: 0.1 },
        transmission: { value: 1, min: 0.9, max: 1, step: 0.01 },
        ior: { value: 1.25, min: 1, max: 2.3, step: 0.05 },
        envMapIntensity: { value: 25, min: 0, max: 100, step: 1 },
        color: '#ffffff',
        attenuationTint: '#ffe79e',
        attenuationDistance: { value: 0, min: 0, max: 1 }
      })
    return (
    <mesh receiveShadow>
        <Html position={[value.pos[0], value.pos[1]+5, value.pos[2]]} center={true} className="innerText" style={{width: count(value.name)+1+'em'}} distanceFactor={30} occlude={true}>
            <p>{value.name}</p>
        </Html>
        <primitive position={[value.pos[0], value.pos[1], value.pos[2]]} object={scene} roughness={0} smoothness={10}/>
        <mesh geometry={nodes.Suzanne.geometry} {...props}>
            <meshPhysicalMaterial {...materialProps} />
        </mesh>
    </mesh>        
    )
}
