import React, {useState} from 'react'
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import {ElementData} from './ElementData';
import { makeStyles } from '@mui/styles';


const useStyles = makeStyles({
    text: {
        color: '#B2BAC2'
    }
});

export const SelectBox = ({itemList, setItemList}) => {

    const style = useStyles();

    const handleChange = (event) => {
        console.log(event);
    };

    return (
        <Box sx={{ minWidth: 120}}>
        <FormControl fullWidth>
            <InputLabel id="demo-simple-select-label" className={style.text}>素子を追加</InputLabel>
            <Select
                value={itemList}
                label="素子を追加"
                onChange={handleChange}
            >
                {ElementData.map((value, key) => {
                    return (
                        <li key={key} className={`row ${value.selected ? "selected" : ""}`}>
                            <div id="icon">{value.icon}</div>
                        </li>
                    )
                })}
                {ElementData.map((item) => {
                    <MenuItem value={item.value}>{item.name}</MenuItem>
                })}
            </Select>
        </FormControl>
        </Box>
    );
}
