import React from 'react'
import * as Drei from "@react-three/drei";
import { Html, Stats } from "@react-three/drei";

// 文字数をカウント (半角に対応)
function count(text){
    var length = 0.0;
    for(var i=0; i < text.length; i++) {
        text[i].match(/[ -~]/) ? length += 0.5 : length += 1.0;
    }
    return length;
}

export const Model3D = ({value}) => {
    const { scene } = Drei.useGLTF(value.input_path);

    return (
    <mesh receiveShadow>
        <mesh geometry={scene} position={[value.pos[0], value.pos[1], value.pos[2]]} roughness={0} smoothness={10}>
            <meshPhysicalMaterial thickness={5} clearcoat={1} transmission={1} envMapIntensity={25} />
        </mesh>
    </mesh>        
    )
}
