import React from 'react'
import { ElementData } from "./ElementData";
import Detail from "./Detail";

export const TodoList = ({taskList, setTaskList}) => {

    const handleCompleted = (id) => {
        setTaskList(taskList.map((task) => {
            if(id == task.id){
                return{
                    ...task, 
                    completed: !task.completed
                };
            }
            return task;
        }))
    };

    const handleDelete = (id) => {
        /* タスクを削除する */
        setTaskList(taskList.filter((task) => task.id !== id));
        console.log(id);
    }

    const handleSelected = (id) => {
        setTaskList(taskList.map((task) => {
            if(id == task.id){
                return{
                    ...task, 
                    selected: !task.selected
                };
            }
            return task;
        }))
    }

    return (
        <div className="todoList">
            <div className="todos">
                {taskList.map((task, index) => (
                        <div className={`todo ${task.completed ? "completed" : ""}`} key={index}>
                            <div className="item">
                                <div 
                                    className={`todoText radiusTopLeft ${task.selected ? "" : "radiusBottomLeft"}`}
                                    onClick={() => handleSelected(task.id)}
                                ><span>{task.text}</span></div>
                                <div className="icons">
                                    <button onClick={() => handleCompleted(task.id)}>

                                    </button>
                                    <button onClick={() => handleDelete(task.id)}>
                                        <i className={`fas fa-trash radiusTopRight ${task.selected ? "" : "radiusBottomRight"}`}></i>
                                    </button>
                                </div>
                            </div>
                        {task.selected ? <Detail/> : ""}
                    </div>
                ))}
            </div>
        </div>
    )
}
