import React from 'react'
import * as Drei from "@react-three/drei";
import { Html, Stats } from "@react-three/drei";

// 文字数をカウント (半角に対応)
function count(text){
    var length = 0.0;
    for(var i=0; i < text.length; i++) {
        text[i].match(/[ -~]/) ? length += 0.5 : length += 1.0;
    }
    return length;
}

export const Model = () => {
    const { scene } = Drei.useGLTF("./models/map_500.glb");
    scene += Drei.useGLTF("./models/map_activity.glb");
    scene += Drei.useGLTF("./models/map_administration.glb");
    scene += Drei.useGLTF("./models/map_agora.glb");
    scene += Drei.useGLTF("./models/map_cafeteria.glb");
    scene += Drei.useGLTF("./models/map_carrer.glb");
    scene += Drei.useGLTF("./models/map_dormitory.glb");
    scene += Drei.useGLTF("./models/map_education.glb");
    scene += Drei.useGLTF("./models/map_gym.glb");
    scene += Drei.useGLTF("./models/map_incubation.glb");
    scene += Drei.useGLTF("./models/map_isc.glb");
    scene += Drei.useGLTF("./models/map_dirmitory.glb");
    const name = "レーザー";
    return (
    <group>
        <Html position={[0,5,0]} className="innerText" style={{width: count(name)+1+'em'}} distanceFactor={30} occlude={true}>
            <p>{name}</p>
        </Html>
        <primitive position={[0,0,0]} object={scene} />
    </group>        
    )
}
